import React from "react";
import Flashcard from "./flashcard";
import Controls from "./controls";
import Stopwatch from "./stopwatch";
import { connect } from "react-redux";
import { toggleControls, updateProbability } from "./actions/index";
import "./App.css";

class App extends React.Component {
	constructor() {
		super();

		this.state = {
			oops: false,
		};

		this.show = this.show.bind(this);
		this.handleOopsClick = this.handleOopsClick.bind(this);
	}

	handleOopsClick() {
		this.setState({ oops: true });
		this.props.dispatch(
			updateProbability(
				this.props.history.note,
				this.props.history.interval,
				this.props.history.probability
			)
		);
	}

	show() {
		return this.props.viewControls ? (
			<Controls />
		) : (
			<div className="d-block">
				<div className="d-flex align-items-center mb-4">
					<div
						id="oops"
						className="border p-3 py-4 mr-5"
						onClick={this.handleOopsClick}
					>
						Oops
					</div>
					<Flashcard />
				</div>
				<Stopwatch />
			</div>
		);
	}

	render() {
		const buttonText = this.props.viewControls
			? "Hide Controls"
			: "Show Controls";
		const appClasses = this.props.viewControls ? "App bg-light" : "App";
		return (
			<div className={appClasses}>
				<nav className="text-left pt-3 container">
					<button
						className="btn btn-primary"
						onClick={() => this.props.dispatch(toggleControls())}
					>
						{buttonText}
					</button>
				</nav>
				<main className="app-main">{this.show()}</main>
			</div>
		);
	}
}

function mapStateToProps({ viewControls, history }) {
	return { viewControls, history };
}

export default connect(mapStateToProps)(App);
