import React from "react";
import { connect } from "react-redux";
import { intervals, notes } from "./config";

class Results extends React.Component {
	constructor() {
		super();
		this.buildChart = this.buildChart.bind(this);
		this.getIntervalsForNote = this.getIntervalsForNote.bind(this);
		this.getNewRow = this.getNewRow.bind(this);
		this.getXAxis = this.getXAxis.bind(this);
	}

	buildChart() {
		console.log(this.props.notes);
		let output = [];
		for (let i = 0; i < notes.length; i++) {
			output.push(this.getNewRow(this.props.notes[i]));
		}

		output.push(this.getXAxis(this.props.intervals[i]));

		return output;
	}

	getIntervalsForNote(note) {
		let output = [];
		const notes = this.props.notes;
		const intervalArray = Object.entries(notes[note.number].intervals).map(
			(el) => el[1]
		);
		console.log();
		for (let i = 0, interval; i < intervalArray.length; i++) {
			interval = intervalArray[i];
			output.push(
				<div>
					{note.name},{interval.name},{interval.probability}
				</div>
			);
		}
		return output;
	}

	getIntervalNames() {
		let output = [];
		for (let i = 0; i < intervals.length; i++) {
			output.push(<div>{interval.name}</div>);
		}
		return output;
	}

	getNewRow(note) {
		return (
			<div className="d-flex justify-content-between">
				<b>{note.name}:</b> <div>{this.getIntervalsForNote(note)}</div>
			</div>
		);
	}

	getXAxis(interval) {
		return (
			<div className="d-flex justify-content-between">
				<b className="hide">X:</b> <div>{this.getIntervalNames()}</div>
			</div>
		);
	}

	render() {
		return <div className="text-dark">{this.buildChart()}</div>;
	}
}

function mapStateToProps({ notes }) {
	return { notes };
}

export default connect(mapStateToProps)(Results);
