import React from "react";
import { connect } from "react-redux";
import { intervals, notes } from "./config";

class Results extends React.Component {
	constructor() {
		super();
		this.buildChart = this.buildChart.bind(this);
		this.createBox = this.createBox.bind(this);
	}

	buildChart() {
		console.log(this.props.notes);
		let output = [];
		for (let i = 0; i < notes.length; i++) {
			output.push([]);
			for (let j = 0; j < intervals.length; j++) {
				output[i].push(this.createBox(i, j));
			}
		}
	}
	createBox(note, interval) {
		return (
			<div>
				{this.props.notes[note].name},{this.props.intervals[interval].name},
				{this.props.intervals[interval].probability}
			</div>
		);
	}

	getIntervalsForNote(note) {
		let output = [];
		const notes = this.props.notes;
		const intervalArray = Object.entries(notes[note].intervals).map(
			(el) => el[1]
		);
		for (let i = 0, interval; i < intervalArray.length; i++) {
			interval = intervalArray[i];
			const { name } = interval;
			output.push(
				<div>
					{this.props.notes[note].name},{this.props.intervals[interval].name},
					{this.props.intervals[interval].probability}
				</div>
			);
		}
		return output;
	}

	render() {
		return <div className="text-dark">{this.buildChart()}</div>;
	}
}

function mapStateToProps({ notes }) {
	return { notes };
}

export default connect(mapStateToProps)(Results);
