import React from "react";
import Flashcard from "./flashcard";
import Controls from "./controls";
import Stopwatch from "./stopwatch";
import { connect } from "react-redux";
import { toggleControls, updateProbability } from "./actions/index";
import "./App.css";

class App extends React.Component {
	constructor() {
		super();

		this.state = {
			oops: false,
		};
		this.handleOopsClick = this.handleOopsClick.bind(this);
	}

	handleOopsClick() {
		this.setState({ oops: true });
		if (this.props.history.note) {
			this.props.dispatch(
				updateProbability(
					this.props.history.note,
					this.props.history.interval,
					this.props.history.probability
				)
			);
		}
	}

	show() {
		return this.props.viewControls ? (
			<Controls />
		) : (
			<div className="d-block">
				<div className="d-flex align-items-center mb-4">
					<Flashcard />
				</div>
				<Stopwatch />
			</div>
		);
	}

	render() {
		let oopsClasses = !this.state.oops ? "border p-3 py-4 mr-5" : "border tiny";
		return (
			<div className={oopsClasses} onClick={this.handleOopsClick} id="oops">
				Oops
			</div>
		);
	}
}

function mapStateToProps({ viewControls, history }) {
	return { viewControls, history };
}

export default connect(mapStateToProps)(App);
